// Generated by CoffeeScript 1.6.1
(function() {

  jQuery(function() {
    $('#subsribe').placeholder();
    $('.search-menu').click(function(e) {
	  $('.head').addClass('active');
    if ($('.head').hasClass('active')){
      $('.search-menu .search').css({
        'display': 'inline-block'
      });
    }

    });
    $('#show-popup').click(function(e) {
      var order_link;
      e.preventDefault();
      order_link = $(this).attr('data-url');
      $('#popup-order .popup-wrap').load(order_link);
      $('#popup-order').show();
      $('#order-form').validate();
    });
    $('body').keypress(function(e) {
      if (e.keyCode === 27) {
        return $('#popup-order').hide();
      }
    });
    $('a.book-gallery').fancybox({
      'autoHeight': true,
      'autoWidth': true
    });
    $('.bullets a').click(function(e) {
      var bullet_id, clicked_bullet, sel;
      e.preventDefault();
      clicked_bullet = $(e.target);
      bullet_id = clicked_bullet.data('article');
      $('.main-article li').hide();
      sel = ".main-article li[data-article=" + bullet_id + "]";
      $(sel).show();
      $('.bullets a').removeClass('active');
      clicked_bullet.addClass('active');
    });

    // Show detailed content
    var detailed_content = $('.detailed-description');
    detailed_content.hide();
    $('*[data-description-show]').click(function(e){
      var _this = $(e.target);
      _this.parent().next('.detailed-description').show();
      _this.hide();
    });

    $('*[data-description-hide]').click(function(e){
      var _this = $(e.target);
      _this.parent().hide();
      $('*[data-description-show]').show();
    });
  });

}).call(this);
